const projects = [
  {
    title: "MediTrack – Medication Reminder App",
    description: "Android app to track and remind users about their medication using Firebase, WorkManager, and SQLite.",
    link: "https://github.com/yourgithub/meditrack",
  },
  {
    title: "Global Football Match Tracker",
    description: "Web app with Node.js, MongoDB, EJS, and JWT for managing global football data and bookmarks.",
    link: "https://github.com/yourgithub/football-tracker",
  },
  {
    title: "City Tourism Guide (Android)",
    description: "Kotlin-based map and navigation app using Google Maps SDK and Places API.",
    link: "https://github.com/yourgithub/tourism-app",
  },
];

const Projects = () => {
  return (
    <section id="projects" className="py-20 bg-gray-100 dark:bg-gray-800 px-4">
      <h2 className="text-4xl font-bold text-center mb-10">Projects</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
        {projects.map((project, index) => (
          <div
            key={index}
            className="bg-white dark:bg-gray-900 shadow-md rounded-lg p-6 hover:shadow-xl transition"
          >
            <h3 className="text-2xl font-semibold mb-2">{project.title}</h3>
            <p className="text-gray-700 dark:text-gray-300 mb-4">{project.description}</p>
            <a
              href={project.link}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 dark:text-blue-400 underline"
            >
              View Project →
            </a>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projects;
