import Projects from './components/Projects';
import Resume from './components/Resume';
import DarkModeToggle from './components/DarkModeToggle';
import TerminalAbout from './components/TerminalAbout';
import Contact from './components/Contact';

function App() {
  return (
    <div className="bg-gradient-to-b from-white via-gray-50 to-blue-100 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 text-black dark:text-white scroll-smooth">
      <header className="sticky top-0 z-50 flex justify-between items-center px-6 py-4 bg-white dark:bg-gray-900 shadow-md dark:shadow-lg">
        <h1 className="text-2xl font-extrabold tracking-tight">
          <span className="text-blue-600 dark:text-blue-400">Prabhkanwar</span> Portfolio
        </h1>
        <DarkModeToggle />
      </header>

      <main className="space-y-28">
        <Hero />
        <Projects />
        <Resume />
        <TerminalAbout />
        <Contact />
      </main>

      <footer className="text-center py-6 text-sm text-gray-600 dark:text-gray-400">
        Â© {new Date().getFullYear()} Prabhkanwar Singh Sabharwal. Built with ðŸ’™ using React & Tailwind CSS.
      </footer>
    </div>
  );
}

export default App;
