import { motion } from 'framer-motion';


const Resume = () => {
  return (
    <section
      id="resume"
      className="py-24 px-4 bg-gray-100 dark:bg-gray-950 text-center"
    >
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true }}
        transition={{ duration: 0.6 }}
        className="text-4xl font-bold mb-10 text-gray-900 dark:text-white"
      >
        ðŸ“„ My Resume
      </motion.h2>

      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        whileInView={{ opacity: 1, scale: 1 }}
        viewport={{ once: true }}
        transition={{ duration: 0.6, delay: 0.2 }}
        className="bg-white dark:bg-gray-900/80 backdrop-blur-lg rounded-2xl shadow-2xl p-4 max-w-4xl mx-auto"
      >
        <img
          src="/resume-preview.png"
          alt="Prabhkanwar Singh Resume Preview"
          className="w-full rounded-xl shadow-lg"
        />

        <div className="mt-6">
          <a
            href="/Prabhkanwar_Singh_Resume.pdf"
            download
            className="inline-flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition text-lg font-medium shadow-md"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-5 w-5"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M4 16v2a2 2 0 002 2h12a2 2 0 002-2v-2M7 10l5 5 5-5M12 15V3"
              />
            </svg>
            Download Resume (PDF)
          </a>
        </div>
      </motion.div>
    </section>
  );
};

export default Resume;
