import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section className="min-h-screen flex flex-col items-center justify-center text-center bg-gradient-to-br from-blue-100 to-blue-300 dark:from-gray-800 dark:to-gray-900 px-4">
      <motion.h1
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-5xl md:text-6xl font-extrabold mb-6 leading-tight"
      >
        Hi, I'm{' '}
        <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-purple-500 dark:from-blue-400 dark:to-purple-400">
          Prabhkanwar
        </span>
      </motion.h1>

      <motion.p
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 0.3 }}
        className="text-lg md:text-xl text-gray-700 dark:text-gray-300 max-w-2xl"
      >
        I'm a full-stack developer passionate about building responsive UIs & efficient backends.
        Currently studying IT Solutions at Humber College ðŸ‡¨ðŸ‡¦. Skilled in JavaScript, React, Node.js,
        Express & native Android (Java).
      </motion.p>

      <motion.a
        href="#projects"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.8 }}
        className="mt-10 px-8 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-full shadow-lg text-lg font-semibold transition duration-300"
      >
        ðŸš€ View My Work
      </motion.a>
    </section>
  );
};

export default Hero;
